internal static extern int pixGetWordsInTextlines (	 IntPtr pixs, 	 int minwidth, 	 int minheight, 	 int maxwidth, 	 int maxheight, 	out IntPtr pboxad, 	out IntPtr ppixad, 	out IntPtr pnai);
